INSERT INTO proxy(
    host, port, proxy_type, cost_time, failed_num, update_time
    , user_name
    , password
    , used_type
    , is_internet
) VALUES (
    #{host}, #{port}, #{proxyType}, #{costTime}, #{failedNum}, #{updateTime}
    , #{userName}
    , #{password}
    , #{usedType}
    , #{isInternet}
) ON DUPLICATE KEY UPDATE
    proxy_type = #{proxyType}, cost_time = #{costTime},
    failed_num = #{failedNum}, update_time = #{updateTime}
    , user_name = #{userName}
    , password = #{password}
    , used_type = #{usedType}
    , is_internet = #{isInternet}

INSERT INTO proxy(
    host, port, proxy_type, cost_time, failed_num, update_time,
