    #{proxy.failedNum}, #{proxy.updateTime}

        , #{proxy.userName}
        , ''


        , #{proxy.password}
        , ''


        , #{proxy.usedType}
        , 1


        , #{proxy.isInternet}
        , 1

    )

ON DUPLICATE KEY UPDATE
    proxy_type = #{proxy.proxyType}, cost_time = #{proxy.costTime},
    failed_num = #{proxy.failedNum}, update_time = #{proxy.updateTime}
